#![cfg(feature = "demo")]
use beacon_api_client::mainnet::Client as BeaconApiClient;
use blst::min_pk::SecretKey as BlsSecretKey;
use eyre::OptionExt;
use std::collections::HashMap;

use crate::crypto::BLSSigner;

pub async fn validator_data(
    beacon_api_client: &BeaconApiClient,
    slot: u64,
) -> Result<(usize, BLSSigner), eyre::Report> {
    let proposer_duties = beacon_api_client.get_proposer_duties(slot / 32).await?.1;

    let proposer_duty = proposer_duties
        .iter()
        .find(|d| d.slot == slot)
        .ok_or_eyre("Failed to find proposer for slot")?;

    let proposer_pk = format!("0x{}", hex::encode(proposer_duty.public_key.to_vec()));

    let validator_sk = validators_pk_to_sk()
        .get(proposer_pk.as_str())
        .ok_or_eyre("Failed to find proposer secret key")?
        .to_string();

    let validator_sk_bytes =
        hex::decode(validator_sk).map_err(|e| eyre::eyre!("Failed to decode hex: {:?}", e))?;

    let validator_bls_sk = BlsSecretKey::from_bytes(&validator_sk_bytes)
        .map_err(|e| eyre::eyre!("Failed to generate secret key from bytes {:?}", e))?;

    let validator_signer = BLSSigner::new(validator_bls_sk);

    Ok((proposer_duty.validator_index, validator_signer))
}

